import { useEffect, useRef } from "react";
import axios from "axios";

function Nodebtlist(){
    useEffect( ()=>{
        const qType = {queryType: "nodebt_detial_all"}

        axios.post("http://reg2intranet.pwa.co.th:9000/nodebt",qType)
        //axios.post("https://reg2.pwa.co.th/reg2metermal/webservice/MeterMalService.php",qType)
        .then((nodebtRes) => {
            for (i = 0; i < nodebtRes.data.length; i ++) {
                var tmp = 
                    {
                        "cus_code":mtr_chg_inf.data[i].cus_code,
                        "org_name":"<span>"+showbranch+"</span>"
                    }
               obj.push(tmp);
              }

              $('#tbMeterMal').DataTable( {
                data: obj,
                "bDestroy": true,
                "order": [[ 1, "desc" ]],
                columns: [
                    { data: 'cus_code' },
                    { data: 'org_name' }
                ]
              });

        });

    },[]);
    return(
        <div>
            <div className="container-fluid">
                <br />
                <div className="row">
                    <div className="col-md-12 col-sm-12 col-12">
                        <div className="card card-primary card-outline">
                        <div className="card-header">
                            <h5 style={{fontWeight: 'bold', color: '#3c8dbc'}}>รายงานผู้ใช้น้ำที่ยังไม่ได้ตั้งหนี้ (ข้อมูล ณ วันที่ <span id="DataDate" /> )</h5>
                        </div>
                        <div className="card-body">
                            <table id="tb_inprogressList" className="compact row-border">
                            <thead>
                                <tr>
                                <th style={{color: 'black'}}>ลำดับ <i className="fas fa-list-ol" /></th>
                                <th style={{color: 'black'}}>สาขา </th>
                                </tr>
                            </thead>
                            <tbody>
                            </tbody>
                            <tfoot>
                                <tr>
                                <th style={{display: 'none'}}>ลำดับ</th>
                                <th style={{display: 'none'}}>สาขา</th>
                                </tr>
                            </tfoot>
                            </table>
                            <span style={{fontSize: 12, color: 'red'}}> *** หมายเหตุ : ข้อมูลแสดงเป็นรายวัน</span>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    );
}

export default Nodebtlist;
