import { useEffect, useRef } from "react";
import axios from "axios";
import $ from 'jquery';
import DataTables from 'datatables.net-dt';

function Nodebtlist(){
    useEffect( ()=>{
        const qType = {queryType: "nodebt_detial_all"}
        var tmp = {};
        var obj = [];
        axios.post("http://reg2intranet.pwa.co.th:9000/nodebt",qType)
        //axios.post("https://reg2.pwa.co.th/reg2metermal/webservice/MeterMalService.php",qType)
        .then((nodebtRes) => {
            for (var i = 0; i < nodebtRes.data.length; i ++) {
                console.log(nodebtRes.data[i])
                var tmp = 
                    {
                        "cus_code":nodebtRes.data[i].CUS_CODE,
                        "org_name":"<span>"+nodebtRes.data[i].CUST_NAME+"</span>"
                    }
               obj.push(tmp);
              }

             $('#tb_nodebtList').DataTable( {
                data: obj,
                "bDestroy": true,
                "order": [[ 1, "desc" ]],
                columns: [
                    { data: 'cus_code' },
                    { data: 'org_name' }
                ]
              });

        });

    },[]);
    return(
        <div>
            <div className="container-fluid">
                <br />
                <div className="row">
                    <div className="col-md-12 col-sm-12 col-12">
                        <div className="card card-primary card-outline">
                        <div className="card-header">
                            <h5 style={{fontWeight: 'bold', color: '#3c8dbc'}}>รายงานผู้ใช้น้ำที่ยังไม่ได้ตั้งหนี้ (ข้อมูล ณ วันที่ <span id="DataDate" /> )</h5>
                        </div>
                        <div className="card-body">
                            <table id="tb_nodebtList" className="compact row-border">
                            <thead>
                                <tr>
                                    <th style={{color: 'black'}}>เลขที่ ผชน. <i className="fas fa-list-ol" /></th>
                                    <th style={{color: 'black'}}>ชื่อ - สกุล </th>
                                    <th style={{color: 'black'}}>สภาพมาตร </th>
                                </tr>
                            </thead>
                            <tbody>
                            </tbody>
                            <tfoot>
                                <tr>
                                    <th style={{display: 'none'}}>ลำดับ</th>
                                    <th style={{display: 'none'}}>สาขา</th>
                                    <th style={{display: 'none'}}>สภาพมาตร</th>
                                </tr>
                            </tfoot>
                            </table>
                            <span style={{fontSize: 12, color: 'red'}}> *** หมายเหตุ : ข้อมูลแสดงเป็นรายวัน</span>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    );
}

export default Nodebtlist;
